public class Bioinf2 {
    // find a substring of a mer size that most frequently occurs
    public static void main(String[] args) {
        String text = "";
        int mer = 11;
        String name = "";

        int old_result = 0;
        String old_value = "";

        for (int indexes1 = 0; indexes1 < text.length(); indexes1++) {
            if (mer + indexes1 <= text.length()) {
                //make a substring of a mer size
                name = text.substring(indexes1, mer + indexes1).intern();

                int count1 = 0;
                for (int indexes2 = 0; indexes2 < text.length(); indexes2++) {
                    if (mer + indexes2 <= text.length()) {
                        String name2 = text.substring(indexes2, mer + indexes2).intern();

                        if (name2 == name) {
                            count1++;
                        }
                    }
                    if (count1 > old_result) {
                        old_result = count1;
                        old_value = name;
                    }
                }
            }

        }
        System.out.println(old_result);
        System.out.println(old_value);
        System.out.println(System.currentTimeMillis());
    }
}
