public class Bioinf3 {
    //create a reverse complementary DNA strand
    public static void main(String[] args) {
        String inputStand = "";
        String outputStrand = "";
        String reversedStrand = "";

        final char a = 'A';
        final char t = 'T';
        final char g = 'G';
        final char c = 'C';


        for (int index = 0; index < inputStand.length(); index++) {
            if (index <= inputStand.length()) {

                switch (inputStand.charAt(index)) {
                    case (a):
                        outputStrand = outputStrand + t;
                        break;
                    case (t):
                        outputStrand = outputStrand + a;
                        break;
                    case (g):
                        outputStrand = outputStrand + c;
                        break;
                    case (c):
                        outputStrand = outputStrand + g;
                        break;
                }
            }
        }

        StringBuilder builder = new StringBuilder();
        builder.append(outputStrand);
        reversedStrand = String.valueOf(builder.reverse());

        System.out.println(reversedStrand);
    }
}
