public class Bioinf4 {
    //returns indexes of positions where pattern starts
    public static void main(String[] args) {

        String genome = "";
        String pattern = "ACTCATTAC";

        for (int indexes = 0; indexes < genome.length(); indexes++) {

            if (pattern.length()+indexes <= genome.length()) {
                String name = genome.substring(indexes, pattern.length() + indexes).intern();

                if (name == pattern) {
                    System.out.print(indexes + " ");
                }
            }
        }
    }
}
